import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ApiService } from 'src/app/apiServices/api.service';
import { Department } from 'src/app/classes/department';

@Component({
  selector: 'app-view-department',
  templateUrl: './view-department.component.html',
  styleUrls: ['./view-department.component.css']
})
export class ViewDepartmentComponent implements OnInit{










  constructor(private apiService:ApiService,private route:Router,private act:ActivatedRoute){}

  viewAllDept:any[]=[];


  ngOnInit(): void {
    this.getAll();
  }


  getAll(){
    this.apiService.get('Admin/getAllDepartments').subscribe((res)=>{
  this.viewAllDept=res;

    })
  }

  gotoGetDeptById(id:number) {
    this.route.navigate(['Admin','department','updateDepartment',id])
    }

    deleteDept(id:number) {
    this.apiService.delete(`Admin/DeletedepartmentDetails/${id}`).subscribe((res)=>{
      location.reload();
    })



     }



gotoAddDepartment() {

  this.route.navigate(['Admin','department','addDepartment'])

}

gotoDepartment() {
  this.route.navigate(['Admin','dashboard'])
  }


}




<button type="button" class="btn btn-danger ms-5 mt-5" (click)="gotoDepartment()">
  Back
  </button>
<button type="button" class="btn btn-success ms-5 mt-5" (click)="gotoAddDepartment()">
Add Department
</button>



<div class="container">
<table class="table">
    <thead>
      <tr>
        <th scope="col">Id</th>
        <th scope="col">DEPARTMENT CODE</th>
        <th scope="col">DEPARTMENT NAME</th>
        <th scope="col">OPTION</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let d of viewAllDept">
        <th scope="row">{{d.id}}</th>
        <td>{{d.departmentCode}}</td>
        <td>{{d.departmentName}}</td>
        <td>
            <button type="button" class="btn btn-primary" (click)="gotoGetDeptById(d.id)">Update</button>
            <button type="button" class="btn btn-danger ms-3"(click)="deleteDept(d.id)">Delete</button>
        </td>
      </tr>

    </tbody>
  </table>
</div>
