import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ApiService } from '../apiServices/api.service';
import { Router } from '@angular/router';





@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {





loginForm: FormGroup;

constructor(private fb: FormBuilder,private ApiService:ApiService,private route:Router) {
  this.loginForm = this.fb.group({
    username: ['', [Validators.required, Validators.email]],
    password: ['', [Validators.required, Validators.minLength(6)]]
  });
}


 onSubmit() {


const data= this.loginForm.value as any;
  this.ApiService.post('Admin/useLoginDetails',data).subscribe(async (res)=>{
    console.log(res);



    localStorage.setItem('loginUser',JSON.stringify(res));

   const role="S"

   console.log(role);

    if(role){
      this.route.navigate(['Admin','dashboard']);
    }else{
      this.route.navigate(['']);
    }


  })

}

}
<nav class="navbar navbar-light">
    <div class="container-fluid">
        <a class="navbar-brand" href="#">
            <img src="/assets/oasys.png" alt="" class="d-inline-block align-text-top">
        </a>
    </div>
</nav>

<div class="container-fluid" style="background-color: violet;">
    <div class="container">
        <div class="card">
            <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">

                <h1 class="login">Log in</h1>
                <div class="inputBox">
                    <input type="text" formControlName="username" required>
                    <span class="user">Usermail</span>
                    <div *ngIf="loginForm.get('username')?.invalid && loginForm.get('username')?.touched" class="error">
                        <small *ngIf="loginForm.get('username')?.errors?.['required']">Usermail is required</small>
                        <small *ngIf="loginForm.get('username')?.errors?.['email']">Invalid usermail format</small>
                    </div>
                </div>

                <div class="inputBox">
                    <input type="password" formControlName="password" required>
                    <span>Password</span>
                    <div *ngIf="loginForm.get('password')?.invalid && loginForm.get('password')?.touched" class="error">
                        <small *ngIf="loginForm.get('password')?.errors?.['required']">Password is required</small>
                        <small *ngIf="loginForm.get('password')?.errors?.['minlength']">Password must be at least 6 characters</small>
                    </div>
                </div>

                <div class="button-container">
                    <button class="enter" type="submit">Enter</button>
                </div>            </form>
        </div>
    </div>
</div>
